def jwtClientId = context.expand( '${#Project#jwtClientId}' )
def jwtSecret = context.expand( '${#Project#jwtSecret}' )

def createJwt(Integer validSeconds, String clientId, String secret) {
    TimeZone.setDefault(TimeZone.getTimeZone('UTC'))
    def rightNowMilli = System.currentTimeMillis()
    def rightNowSec = Math.round(rightNowMilli / 1000)
    def expirationSec = rightNowSec + validSeconds
    def jtiValue = UUID.randomUUID().toString()

    Map header = [alg: "HS256", typ: "JWT"]
    def headerJson = JsonOutput.toJson(header)
    def headerBase64 = headerJson.getBytes("UTF-8").encodeBase64().toString().split("=")[0].replaceAll("\\+", "-").replaceAll("/", "_")

    Map payload = [exp: expirationSec, iat: rightNowSec, iss: clientId, client_id: clientId, user_id: clientId, user_reresentation: clientId, jti: jtiValue]
    def payloadJson = JsonOutput.toJson(payload)
    def payloadBase64 = payloadJson.getBytes("UTF-8").encodeBase64().toString().split("=")[0].replaceAll("\\+", "-").replaceAll("/", "_")

    def toBeSigned = headerBase64 + "." + payloadBase64
    SecretKeySpec secretKeySpec = new SecretKeySpec(secret.getBytes("UTF-8"), "HmacSHA256")

    Mac mac = Mac.getInstance("HmacSHA256")
    mac.init(secretKeySpec)
    byte[] digest = mac.doFinal(toBeSigned.getBytes("UTF-8"))
    def signature = digest.encodeBase64().toString().split("=")[0].replaceAll("\\+", "-").replaceAll("/", "_")

    headerBase64 + "." + payloadBase64 + "." + signature
}

// Create the JWT.
def token = createJwt(600, jwtClientId, jwtSecret)
testRunner.testCase.getTestStepByName("Properties").setPropertyValue( 'JwtToken', "Bearer " + token)

con:configuration.scriptText:
import groovy.xml.*

// Parse the XML response
def response = messageExchange.getResponseContent()
def xml = new XmlSlurper().parseText(response)

// Get the values of "einddatum" and "datumStatusGezet" elements
def einddatum = xml.'**'.find { it.name() == 'einddatum' }?.text()
def datumStatusGezet = xml.'**'.find { it.name() == 'datumStatusGezet' }?.text()

// Check if the value of "datumStatusGezet" dind't take the value of "einddatum" since it has own value
assert !(datumStatusGezet.contains('000000')) : "'datumStatusGezet' took the value of 'einddatum'"

