con:soapui-project.con:beforeSaveScript:
def clearProperties(obj) {
    for (prop in obj.propertyList) {
        if (prop.getValue() && !prop.getValue().startsWith('$')){   // value doesn't contain property expansion
            prop.setValue("")
        }
    }
}

for (testSuite in project.getTestSuiteList()) {
    log.info("Clearing custom properties on TestSuite: ${testSuite.getName()}")
    clearProperties(testSuite)

    for (testCase in testSuite.getTestCaseList()) {
        def propertiesTestStep = testCase.getTestStepByName("Properties");
        	if(propertiesTestStep != null){
			clearProperties(propertiesTestStep)
			log.info("Clearing Properties teststep on: ${testSuite.getName()}:${testCase.getName()}")
        	}
    }
}
